// src/pages/PaymentPage.jsx
import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './PaymentPage.css';

const PaymentPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const queryParams = new URLSearchParams(location.search);

  // Get tour details from URL parameters
  const tourId = queryParams.get('tour');
  const tourName = queryParams.get('name') || "Cultural Triangle Explorer";
  const duration = parseInt(queryParams.get('duration')) || 3;
  const pricePerDay = parseInt(queryParams.get('pricePerDay')) || 80;
  const basePrice = parseInt(queryParams.get('basePrice')) || 240;

  const [paymentData, setPaymentData] = useState({
    cardNumber: '',
    cardName: '',
    expiryDate: '',
    cvv: '',
    saveCard: false
  });

  const [customization, setCustomization] = useState({
    participants: 2,
    selectedDuration: duration,
    extraServices: {
      guide: false,
      transport: false,
      meals: false,
      accommodation: false
    }
  });

  // Calculate prices based on customization
  const calculatePrices = () => {
    const baseTourPrice = customization.selectedDuration * pricePerDay * customization.participants;

    let extraServicesCost = 0;
    if (customization.extraServices.guide) extraServicesCost += 50 * customization.participants;
    if (customization.extraServices.transport) extraServicesCost += 100;
    if (customization.extraServices.meals) extraServicesCost += 30 * customization.participants * customization.selectedDuration;
    if (customization.extraServices.accommodation) extraServicesCost += 60 * customization.participants * customization.selectedDuration;

    const subtotal = baseTourPrice + extraServicesCost;
    const serviceFee = 15;
    const total = subtotal + serviceFee;

    return { baseTourPrice, extraServicesCost, subtotal, serviceFee, total };
  };

  const prices = calculatePrices();

  const handleSubmit = (e) => {
    e.preventDefault();

    // Check if user is logged in
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please login to book a tour');
      navigate('/login');
      return;
    }

    console.log('Payment submitted:', paymentData);
    console.log('Tour customization:', customization);

    alert('Payment Successful! Your booking is confirmed.');
    navigate('/');
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setPaymentData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleCustomizationChange = (e) => {
    const { name, value, type, checked } = e.target;

    if (name.startsWith('extra-')) {
      const serviceName = name.replace('extra-', '');
      setCustomization(prev => ({
        ...prev,
        extraServices: {
          ...prev.extraServices,
          [serviceName]: checked
        }
      }));
    } else {
      setCustomization(prev => ({
        ...prev,
        [name]: type === 'checkbox' ? checked : parseInt(value)
      }));
    }
  };

  const formatCardNumber = (value) => {
    const v = value.replace(/\s+/g, '').replace(/[^0-9]/gi, '');
    const matches = v.match(/\d{4,16}/g);
    const match = (matches && matches[0]) || '';
    const parts = [];

    for (let i = 0, len = match.length; i < len; i += 4) {
      parts.push(match.substring(i, i + 4));
    }

    if (parts.length) {
      return parts.join(' ');
    } else {
      return value;
    }
  };

  const handleCardNumberChange = (e) => {
    const formatted = formatCardNumber(e.target.value);
    setPaymentData(prev => ({
      ...prev,
      cardNumber: formatted
    }));
  };

  return (
    <div className="payment-page">
      <div className="container">
        <div className="payment-header">
          <h1 className="page-title">Secure Payment</h1>
          <p className="page-subtitle">Complete your booking with secure payment</p>
        </div>

        <div className="payment-content">
          {/* Order Summary */}
          <div className="order-summary">
            <div className="summary-header">
              <h2 className="summary-title">Order Summary</h2>
            </div>

            <div className="tour-details">
              <h3 className="tour-name">{decodeURIComponent(tourName)}</h3>

              {/* Customization Controls */}
              <div className="customization-controls">
                <div className="form-group">
                  <label htmlFor="participants" className="form-label">Number of Participants</label>
                  <input
                    type="number"
                    id="participants"
                    name="participants"
                    min="1"
                    max="30"
                    value={customization.participants}
                    onChange={handleCustomizationChange}
                    className="form-input"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="selectedDuration" className="form-label">Tour Duration (Days)</label>
                  <input
                    type="number"
                    id="selectedDuration"
                    name="selectedDuration"
                    min="1"
                    max="30"
                    value={customization.selectedDuration}
                    onChange={handleCustomizationChange}
                    className="form-input"
                  />
                </div>

                <div className="extra-services">
                  <h4 className="services-title">Extra Services</h4>
                  <div className="services-grid">
                    <label className="checkbox service-checkbox">
                      <input
                        type="checkbox"
                        name="extra-guide"
                        checked={customization.extraServices.guide}
                        onChange={handleCustomizationChange}
                      />
                      <span>Professional Guide ($50/person)</span>
                    </label>

                    <label className="checkbox service-checkbox">
                      <input
                        type="checkbox"
                        name="extra-transport"
                        checked={customization.extraServices.transport}
                        onChange={handleCustomizationChange}
                      />
                      <span>Private Transport ($100)</span>
                    </label>

                    <label className="checkbox service-checkbox">
                      <input
                        type="checkbox"
                        name="extra-meals"
                        checked={customization.extraServices.meals}
                        onChange={handleCustomizationChange}
                      />
                      <span>Full Board Meals ($30/person/day)</span>
                    </label>

                    <label className="checkbox service-checkbox">
                      <input
                        type="checkbox"
                        name="extra-accommodation"
                        checked={customization.extraServices.accommodation}
                        onChange={handleCustomizationChange}
                      />
                      <span>Accommodation ($60/person/day)</span>
                    </label>
                  </div>
                </div>
              </div>

              <div className="tour-info">
                <div className="info-row">
                  <span className="info-label">Price per day:</span>
                  <span className="info-value">${pricePerDay}/person</span>
                </div>
                <div className="info-row">
                  <span className="info-label">Selected duration:</span>
                  <span className="info-value">{customization.selectedDuration} days</span>
                </div>
                <div className="info-row">
                  <span className="info-label">Participants:</span>
                  <span className="info-value">{customization.participants} persons</span>
                </div>
              </div>
            </div>

            <div className="price-breakdown">
              <div className="price-row">
                <span>Base Tour Price:</span>
                <span>${prices.baseTourPrice}</span>
              </div>

              {prices.extraServicesCost > 0 && (
                <div className="price-row">
                  <span>Extra Services:</span>
                  <span>${prices.extraServicesCost}</span>
                </div>
              )}

              <div className="price-row">
                <span>Subtotal:</span>
                <span>${prices.subtotal}</span>
              </div>

              <div className="price-row">
                <span>Service Fee:</span>
                <span>${prices.serviceFee}</span>
              </div>

              <div className="price-total">
                <span>Total Amount:</span>
                <span className="total-amount">${prices.total}</span>
              </div>
            </div>
          </div>

          {/* Payment Form */}
          <div className="payment-form-section">
            <form onSubmit={handleSubmit} className="payment-form">
              <div className="form-section">
                <h3 className="section-title">Card Details</h3>

                <div className="form-group">
                  <label htmlFor="cardNumber" className="form-label">Card Number</label>
                  <input
                    type="text"
                    id="cardNumber"
                    name="cardNumber"
                    value={paymentData.cardNumber}
                    onChange={handleCardNumberChange}
                    className="form-input"
                    required
                    placeholder="1234 5678 9012 3456"
                    maxLength="19"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="cardName" className="form-label">Name on Card</label>
                  <input
                    type="text"
                    id="cardName"
                    name="cardName"
                    value={paymentData.cardName}
                    onChange={handleChange}
                    className="form-input"
                    required
                    placeholder="John Doe"
                  />
                </div>

                <div className="form-row">
                  <div className="form-group">
                    <label htmlFor="expiryDate" className="form-label">Expiry Date</label>
                    <input
                      type="text"
                      id="expiryDate"
                      name="expiryDate"
                      value={paymentData.expiryDate}
                      onChange={handleChange}
                      className="form-input"
                      required
                      placeholder="MM/YY"
                      maxLength="5"
                    />
                  </div>

                  <div className="form-group">
                    <label htmlFor="cvv" className="form-label">CVV</label>
                    <input
                      type="text"
                      id="cvv"
                      name="cvv"
                      value={paymentData.cvv}
                      onChange={handleChange}
                      className="form-input"
                      required
                      placeholder="123"
                      maxLength="4"
                    />
                  </div>
                </div>

                <label className="checkbox">
                  <input
                    type="checkbox"
                    name="saveCard"
                    checked={paymentData.saveCard}
                    onChange={handleChange}
                  />
                  <span>Save card for future payments</span>
                </label>
              </div>

              <div className="form-section">
                <h3 className="section-title">Billing Address</h3>

                <div className="form-group">
                  <label htmlFor="address" className="form-label">Address</label>
                  <input
                    type="text"
                    id="address"
                    name="address"
                    className="form-input"
                    required
                    placeholder="Enter your address"
                  />
                </div>

                <div className="form-row">
                  <div className="form-group">
                    <label htmlFor="city" className="form-label">City</label>
                    <input
                      type="text"
                      id="city"
                      name="city"
                      className="form-input"
                      required
                      placeholder="Enter city"
                    />
                  </div>

                  <div className="form-group">
                    <label htmlFor="zip" className="form-label">ZIP Code</label>
                    <input
                      type="text"
                      id="zip"
                      name="zip"
                      className="form-input"
                      required
                      placeholder="ZIP code"
                    />
                  </div>
                </div>
              </div>

              <div className="terms-agreement">
                <label className="checkbox">
                  <input type="checkbox" required />
                  <span>
                    I agree to the <a href="/terms">Terms & Conditions</a> and{' '}
                    <a href="/privacy">Privacy Policy</a>
                  </span>
                </label>
              </div>

              <button type="submit" className="submit-btn">
                Pay ${prices.total}
              </button>

              <p className="security-note">
                 Your payment is secured with SSL encryption
              </p>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PaymentPage;
