import React from 'react';
import { useNavigate } from 'react-router-dom';
import GuideCard from '../common/GuideCard';
import SriLankaMap from '../common/SriLankaMap';
import SearchBar from '../common/SearchBar';
import SigiriyaImage from '../assets/Sigiriya.jpg';
import './HomePage.css';

// Import placeholder images (you can replace these with actual images)
const placeholderGuide1 = 'https://images.unsplash.com/photo-1564564321837-a57b7070ac4f?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&q=80';
const placeholderGuide2 = 'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&q=80';
const placeholderGuide3 = 'https://images.unsplash.com/photo-1494790108755-2616b612b786?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&q=80';
const ellaTrainImage = 'https://images.unsplash.com/photo-1603455771989-c8dd8d1599b3?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&q=80';
const teaPlantationImage = 'https://images.unsplash.com/photo-1561715276-a2d087060f1d?ixlib=rb-4.0.3&auto=format&fit=crop&w-600&q=80';
const unescoImage = 'https://images.unsplash.com/photo-1554491475-1dd16950258d?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&q=80';

const HomePage = () => {
  const navigate = useNavigate();

  const featuredGuides = [
    {
      id: 1,
      name: "Kamal Perera",
      image: placeholderGuide1,
      location: "Colombo, Sri Lanka",
      languages: ["English", "Sinhala", "Tamil"],
      rating: 4.9,
      price: 45,
      reviews: 127,
      specialties: ["City Tours", "Food", "History"],
      verified: true,
      experience: "8 years"
    },
    {
      id: 2,
      name: "Nimal Fernando",
      image: placeholderGuide2,
      location: "Kandy, Sri Lanka",
      languages: ["English", "Sinhala"],
      rating: 4.8,
      price: 40,
      reviews: 98,
      specialties: ["Cultural Tours", "Temples", "Heritage"],
      verified: true,
      experience: "6 years"
    },
    {
      id: 3,
      name: "Sunil Rajapaksa",
      image: placeholderGuide3,
      location: "Galle, Sri Lanka",
      languages: ["English", "Sinhala", "French"],
      rating: 5.0,
      price: 50,
      reviews: 156,
      specialties: ["Beach Tours", "Fort Walks", "Photography"],
      verified: true,
      experience: "10 years"
    },
  ];

  const popularDestinations = [
    {
      id: 1,
      name: "Sigiriya Rock Fortress",
      image: SigiriyaImage, // Using the same Sigiriya image
      description: "Ancient rock fortress and UNESCO World Heritage site",
      features: ["Historical", "UNESCO", "Adventure"]
    },
    {
      id: 2,
      name: "Ella Train Journey",
      image: ellaTrainImage,
      description: "Scenic train ride through tea plantations and mountains",
      features: ["Scenic", "Relaxing", "Photography"]
    },
    {
      id: 3,
      name: "Tea Plantations",
      image: teaPlantationImage,
      description: "Visit working tea estates in Nuwara Eliya",
      features: ["Cultural", "Educational", "Tasting"]
    },
  ];

  const whyChooseUs = [
    {
      id: 1,
      title: "Best Locations",
      description: "Explore UNESCO World Heritage Sites and hidden gems across Sri Lanka",
      icon: "üìç"
    },
    {
      id: 2,
      title: "Expert Guides",
      description: "Professional, licensed tour guides with extensive local knowledge",
      icon: "üë®‚Äçüè´"
    },
    {
      id: 3,
      title: "Personalized Tours",
      description: "Customized itineraries tailored to your interests and schedule",
      icon: "üìÖ"
    },
  ];

  return (
    <div className="home-page">
      {/* Hero Section */}
      <section className="hero-section" style={{
        backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.4)), url(${SigiriyaImage})`,
      }}>
        <div className="hero-overlay">
          <div className="container">
            <div className="hero-content">
              <h1 className="hero-title">
                Discover Sri Lanka:<br />The Pearl of the Indian Ocean Awaits Your Exploration
              </h1>
              <p className="hero-subtitle">
                Experience authentic Sri Lanka with our expert local guides. From ancient 
                ruins to pristine beaches, create unforgettable memories with personalized tours.
              </p>

              {/* Search Bar */}
              <div className="search-container">
                <SearchBar onSearch={(term) => navigate(`/guides?search=${term}`)} />
              </div>

              {/* Hero Buttons */}
              <div className="hero-buttons">
                <button 
                  className="btn-primary" 
                  onClick={() => navigate('/guides')}
                >
                  Explore Tours
                </button>
                <button 
                  className="btn-secondary"
                  onClick={() => navigate('/contact')}
                >
                  Contact Us
                </button>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Why Choose Us Section */}
      <section className="section why-choose-us">
        <div className="container">
          <div className="section-header">
            <h2 className="section-title">Why Choose Ceylon Tours?</h2>
            <p className="section-subtitle">Experience the best of Sri Lanka with our trusted services</p>
          </div>
          <div className="features-grid">
            {whyChooseUs.map((feature) => (
              <div key={feature.id} className="feature-card">
                <div className="feature-icon">{feature.icon}</div>
                <h3>{feature.title}</h3>
                <p>{feature.description}</p>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Popular Destinations */}
      <section className="section popular-destinations bg-light">
        <div className="container">
          <div className="section-header">
            <h2 className="section-title">Popular Destinations</h2>
            <p className="section-subtitle">Explore Sri Lanka's most iconic locations</p>
          </div>
          <div className="destinations-grid">
            {popularDestinations.map((destination) => (
              <div 
                key={destination.id} 
                className="destination-card"
                onClick={() => navigate(`/destinations/${destination.name.toLowerCase().replace(/\s+/g, '-')}`)}
                role="button"
                tabIndex={0}
                onKeyPress={(e) => e.key === 'Enter' && navigate(`/destinations/${destination.name.toLowerCase().replace(/\s+/g, '-')}`)}
              >
                <div 
                  className="destination-image"
                  style={{ backgroundImage: `url(${destination.image})` }}
                >
                  <div className="destination-overlay">
                    <h3>{destination.name}</h3>
                  </div>
                </div>
                <div className="destination-content">
                  <p>{destination.description}</p>
                  <div className="destination-tags">
                    {destination.features.map((feature, index) => (
                      <span key={index} className="tag">{feature}</span>
                    ))}
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* How It Works */}
      <section className="section how-it-works">
        <div className="container">
          <div className="section-header">
            <h2 className="section-title">Simple Booking Process</h2>
            <p className="section-subtitle">Book your perfect guide in 3 easy steps</p>
          </div>
          <div className="steps">
            <div className="step">
              <div className="step-number">1</div>
              <h3>Search & Select</h3>
              <p>Find guides by location, language, or specialty</p>
            </div>
            <div className="step">
              <div className="step-number">2</div>
              <h3>Customize</h3>
              <p>Choose dates and create your perfect itinerary</p>
            </div>
            <div className="step">
              <div className="step-number">3</div>
              <h3>Book & Explore</h3>
              <p>Secure booking with 24/7 support</p>
            </div>
          </div>
        </div>
      </section>

      {/* Featured Guides */}
      <section className="section featured-guides">
        <div className="container">
          <div className="section-header">
            <h2 className="section-title">Top Rated Guides</h2>
            <p className="section-subtitle">Highly recommended by travelers</p>
          </div>
          <div className="guides-grid">
            {featuredGuides.map(guide => (
              <GuideCard key={guide.id} guide={guide} />
            ))}
          </div>
          <div className="text-center">
            <button 
              className="btn-primary" 
              onClick={() => navigate('/guides')}
            >
              View All Guides
            </button>
          </div>
        </div>
      </section>

      {/* Interactive Map */}
      <section className="section map-section bg-light">
        <div className="container">
          <div className="section-header">
            <h2 className="section-title">Explore Sri Lanka</h2>
            <p className="section-subtitle">Discover amazing destinations across the island</p>
          </div>
          <SriLankaMap />
        </div>
      </section>

      {/* CTA Section */}
      <section className="section cta-section">
        <div className="container">
          <div className="cta-content">
            <h2>Ready for Your Sri Lankan Adventure?</h2>
            <p>Book your guide today and create unforgettable memories</p>
            <button 
              className="btn-primary" 
              onClick={() => navigate('/guides')}
            >
              Start Booking Now
            </button>
          </div>
        </div>
      </section>
    </div>
  );
};

export default HomePage;
